mapper.select:
SELECT gOrder.ID, gOrder.CODE, gOrder.STATUS, gOrder.SUBMIT_TIME AS SUBMIT_TIME, gOrder.CREATE_TIME AS CREATE_TIME, gCustomer.ID AS CUSTOMER_ID, gCustomer.NAME AS CUSTOMER_NAME, gCustomer.PHONE AS CUSTOMER_PHONE, gCustomer.SEX AS CUSTOMER_SEX, gCustomer.BIRTHDAY AS CUSTOMER_BIRTHDAY, gAgency.ID AS AGENCY_ID, gAgency.NAME AS AGENCY_NAME, gtProduct.ID AS PRODUCT_ID, gtProduct.NAME AS PRODUCT_NAME, gtProduct.CODE AS PRODUCT_CODE FROM GHEALTH_ORDER gOrder LEFT JOIN ghealth_customer gCustomer ON gOrder.CUSTOMER_ID = gCustomer.ID LEFT JOIN ghealth_agency gAgency ON gOrder.AGENCY_ID = gAgency.ID LEFT JOIN ghealth_testing_product gtProduct ON gOrder.PRODUCT_ID = gtProduct.ID WHERE gOrder.CODE IN
#{code}
AND gOrder.DELETED = 0 AND gOrder.STATUS != 0 AND gOrder.STATUS != 6 ORDER BY gOrder.CREATE_TIME DESC
